package stdioImporter.runtime;

/*Generated by MPS */

import stdio_parser.CodeGenerator;
import stdio_parser.parser;
import stdio_parser.Lexer;
import java.io.FileReader;

public class ParserAdapter {
  public static CodeGenerator Parse(String filename) {
    try {
      CodeGenerator cg = new CodeGenerator();
      String tempfile = "temp.h";
      // removing c++ parts from the input file and saving it in a temprorary file 
      cg.removeCPP(filename, tempfile);
      parser p = new parser(new Lexer(new FileReader(tempfile)));
      p.parse();
      return p.cg;
    } catch (Exception e) {
      System.out.println("ERROR in Parsing");
    }
    return null;
  }
}
